import React, { useMemo, useState } from "react";
import { getTeamsSorted } from '../../utils/rankings';
import { useRankings } from "../../context/RankingsContext";
import { PlayerRankingsProvider, usePlayerRankings } from "../../context/PlayerRankingsContext";
import "./RankingsTabs.css";

const Tabs = ({ value, onChange, items }) => (
  <div className="tabs">
    {items.map(it => (
      <button
        key={it.value}
        type="button"
        className={`tab ${value === it.value ? "active" : ""}`}
        onClick={() => onChange(it.value)}
      >
        {it.label}
      </button>
    ))}
  </div>
);

const TeamTable = ({ format, top = 0, gender }) => {
  const { rankings, loading } = useRankings();

  const rows = useMemo(() => {
    const sorted = getTeamsSorted(rankings, format, gender);
    if (!sorted.length) return [];

    return top > 0 ? sorted.slice(0, top) : sorted;
  }, [rankings, format, top]);

  if (loading) return <div className="rk-loading">Loading…</div>;
  if (!rows.length) {
    return (
      <div className="rk-empty">
        {gender === "womens"
          ? "No women's team rankings available."
          : "No team rankings available."}
      </div>
    );
  }

  const [first, ...rest] = rows;

  return (
    <div className="rk-table">
      <div className="rk-banner">
        <div className="rk-top">
          <img src={first.image_path} alt={first.name} className="rk-flag-lg" />
          <div className="rk-top-meta">
            <div className="rk-top-name">{first.name}</div>
            <div className="rk-top-points">{first.ranking.points} Points</div>
          </div>
          <div className="rk-top-pos">#1</div>
        </div>
      </div>

      <div className="rk-rows">
        {rest.map((t, i) => (
          <div className="rk-row" key={t.id}>
            <span className="rk-pos">{i + 2}</span>
            <span className="rk-flag-col">
              <img src={t.image_path} alt={t.name} className="rk-flag-sm" />
            </span>
            <span className="rk-name">{t.name}</span>
            <span className="rk-points">{t.ranking.points}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

const PlayerTableInner = ({ top = 0 }) => {
  const { players, loading } = usePlayerRankings();
  if (loading) return <div className="rk-loading">Loading…</div>;
  if (!players.length) return <div className="rk-empty">No player rankings available.</div>;

  const rows = top > 0 ? players.slice(0, top) : players;
  const [first, ...rest] = rows;

  return (
    <div className="rk-table">
      <div className="rk-banner">
        <div className="rk-top">
          <div className="rk-top-meta">
            <div className="rk-top-name">{first.name}</div>
            <div className="rk-top-sub">
              {first.team} · Rating {first.rating ?? "—"}
            </div>
          </div>
          <div className="rk-top-pos">#{first.position}</div>
        </div>
      </div>

      <div className="rk-rows">
        {rest.map(p => (
          <div className="rk-row" key={`${p.name}-${p.team}`}>
            <span className="rk-pos">{p.position}</span>
            <span className="rk-name">{p.name}</span>
            <span className="rk-team">{p.team}</span>
            <span className="rk-points">{p.rating ?? "—"}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

const PlayerTable = ({ gender, format, role, top = 0 }) => (
  <PlayerRankingsProvider gender={gender} format={format} role={role} limit={Math.max(top, 50)}>
    <PlayerTableInner top={top} />
  </PlayerRankingsProvider>
);

const RankingsTabsHierarchical = ({ compactTop = 0 }) => {
  // level 1
  const [view, setView] = useState("teams");   // 'teams' | 'players'
  // level 2
  const [gender, setGender] = useState("mens"); // 'mens' | 'womens'
  // level 3
  const [format, setFormat] = useState("T20I"); // 'T20I' | 'ODI' | 'TEST'
  // players only
  const [role, setRole] = useState("batting");  // 'batting' | 'bowling' | 'allrounder'

  // adapt for player endpoint (expects lowercased test/odi/t20i)
  const playerFormat = format.toLowerCase();

  return (
    <div className="rk-tab-group">
      {/* 1st Row: Teams | Players */}
      <div className="rk-tab-row">
        <Tabs
          value={view}
          onChange={setView}
          items={[
            { value: "teams", label: "Teams" },
            { value: "players", label: "Players" }
          ]}
        />
      </div>

      {/* 2nd Row: Men | Women */}
      <div className="rk-tab-row">
        <Tabs
          value={gender}
          onChange={setGender}
          items={[
            { value: "mens", label: "Men" },
            { value: "womens", label: "Women" }
          ]}
        />
      </div>

      {/* 3rd Row: Formats */}
      <div className="rk-tab-row">
        <Tabs
          value={format}
          onChange={setFormat}
          items={[
            { value: "T20I", label: "T20I" },
            { value: "ODI", label: "ODI" },
            { value: "TEST", label: "TEST" }
          ]}
        />
      </div>

      {/* Optional: Player Role Tabs */}
      {view === "players" && (
        <div className="rk-tab-row">
          <Tabs
            value={role}
            onChange={setRole}
            items={[
              { value: "batting", label: "Batting" },
              { value: "bowling", label: "Bowling" },
              { value: "allrounder", label: "All-rounder" }
            ]}
          />
        </div>
      )}

      <div className="rk-content">
        {view === "teams" ? (
          <TeamTable gender={gender} format={format} top={compactTop} />
        ) : (
          <PlayerTable gender={gender} format={playerFormat} role={role} top={compactTop} />
        )}
      </div>
    </div>
  );
};

export default RankingsTabsHierarchical;
