import React, { useEffect, useRef, useState } from 'react';
import {
  liveStatuses,
  upcomingMatchStatuses,
  completedMatchStatuses,
} from './../../constants/matchStatusConstants';
import MatchCard from './../MatchCard';
import './css/FeaturedMatches.css';

const FeaturedMatches = () => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);

  // Buckets
  const [liveMatches, setLiveMatches] = useState([]);
  const [upcomingMatches, setUpcomingMatches] = useState([]);
  const [completedMatches, setCompletedMatches] = useState([]);

  // Stages (for chip filters)
  const [stages, setStages] = useState([]);
  const [activeStage, setActiveStage] = useState(null); // null => All Featured Matches

  // Carousel state
  const scrollRef = useRef(null);
  const [currentPage, setCurrentPage] = useState(0);
  const [pageCount, setPageCount] = useState(1);

  // ---- Fetch schedule once ----
  useEffect(() => {
    let isMounted = true;
    (async () => {
      try {
        const res = await fetch('/api/schedule');
        const json = await res.json();
        if (!isMounted) return;
        setMatches(json?.data || []);
      } catch (e) {
        console.error('Error fetching schedule:', e);
      } finally {
        if (isMounted) setLoading(false);
      }
    })();
    return () => { isMounted = false; };
  }, []);

  // ---- Build buckets & stages whenever matches change ----
  useEffect(() => {
    if (loading) return;

    const now = Date.now();

    const live = matches.filter(m =>
      liveStatuses.includes(m.status)
      && m.matchStarted === true
      && m.matchEnded === false
      && (m.status || '').toLowerCase() !== 'match not started'
    );

    const upcoming = matches
      .filter(m => upcomingMatchStatuses.includes(m.status))
      .sort((a, b) => new Date(a.starting_at) - new Date(b.starting_at))
      .slice(0, 10); // next 10 upcoming

    const completed = matches.filter(m => {
      const d = new Date(m.starting_at).getTime();
      return completedMatchStatuses.includes(m.status)
        && (now - d) <= 2 * 24 * 60 * 60 * 1000; // last 2 days
    });

    const allStages = Array.from(
      new Set(matches.map(m => m?.stage?.name).filter(Boolean))
    );

    setLiveMatches(live);
    setUpcomingMatches(upcoming);
    setCompletedMatches(completed);
    setStages(allStages);
  }, [matches, loading]);

  // ---- Derive list to render, respecting stage filter ----
  const allFeatured = React.useMemo(() => {
    const concat = [...liveMatches, ...upcomingMatches, ...completedMatches];
    if (!activeStage) return concat;
    return concat.filter(m => (m?.stage?.name || '') === activeStage);
  }, [liveMatches, upcomingMatches, completedMatches, activeStage]);

  // ---- Carousel paging: compute pages from DOM size ----
  useEffect(() => {
    const el = scrollRef.current;
    if (!el) return;

    const computePages = () => {
      // pages = ceil(total scrollable width / viewport width)
      const pages = Math.max(1, Math.ceil(el.scrollWidth / Math.max(1, el.clientWidth)));
      setPageCount(pages);
      // keep currentPage in range
      setCurrentPage(prev => Math.min(prev, pages - 1));
    };

    const handleScroll = () => {
      const page = Math.round(el.scrollLeft / Math.max(1, el.clientWidth));
      setCurrentPage(page);
    };

    computePages();
    el.addEventListener('scroll', handleScroll);
    window.addEventListener('resize', computePages);

    return () => {
      el.removeEventListener('scroll', handleScroll);
      window.removeEventListener('resize', computePages);
    };
  }, [allFeatured]);

  // ---- Scroll to page when clicking dot ----
  const scrollToPage = (pageIndex) => {
    const el = scrollRef.current;
    if (!el) return;
    const x = pageIndex * el.clientWidth;
    el.scrollTo({ left: x, behavior: 'smooth' });
  };

  if (loading) {
    return (
      <div className="featured-container">
        <div className="featured-tabs-row">
          <div className="featured-tabs-left">
            <span className="tab active-tab">All Featured Matches</span>
          </div>
          <a href="/schedule" className="see-all-btn">All</a>
        </div>
        <div style={{ opacity: 0.75, padding: '8px 4px' }}>Loading…</div>
      </div>
    );
  }

  return (
    <div className="featured-container">
      {/* Chips row */}
      <div className="featured-tabs-row">
        <div className="featured-tabs-left">
          <span
            className={`tab ${!activeStage ? 'active-tab' : ''}`}
            onClick={() => setActiveStage(null)}
            title="All Featured Matches"
          >
            All Featured Matches
          </span>

          {stages.map((stage) => (
            <span
              key={stage}
              className={`tab ${activeStage === stage ? 'active-tab' : ''}`}
              onClick={() => setActiveStage(stage)}
              title={stage}
            >
              {stage}
            </span>
          ))}
        </div>

        <a href="/schedule" className="see-all-btn" title="Go to full Schedule">
          All
        </a>
      </div>

      {/* Cards carousel */}
      <div className="match-cards-scroll-wrapper" ref={scrollRef}>
        <div className={`match-cards-scroll ${allFeatured.length < 3 ? 'center' : ''}`}>
          {allFeatured.map(match => (
            <MatchCard key={match.id} match={match} />
          ))}
        </div>
      </div>

      {/* Pagination dots */}
      <div className="pagination-dots">
        {Array.from({ length: pageCount }).map((_, i) => (
          <span
            key={i}
            className={`dot ${i === currentPage ? 'active' : ''}`}
            onClick={() => scrollToPage(i)}
            style={{ cursor: 'pointer' }}
            aria-label={`Go to page ${i + 1}`}
          />
        ))}
      </div>
    </div>
  );
};

export default FeaturedMatches;
