import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import { useSeries } from './../context/SeriesContext';
import { format } from 'date-fns';
import './css/Series.css';

export default function Series() {
  const { allSeries, loading } = useSeries();
  const [search, setSearch] = useState('');

  // ---- Helpers ----
  const formatDate = (dateStr) => {
    if (!dateStr) return '';
    const d = new Date(dateStr);
    return isNaN(d.getTime()) ? '' : format(d, 'dd MMM yyyy');
  };

  const adjustedEndDate = (series) => {
    if (!series?.end_date) return '';
    const d = new Date(series.end_date);
    const code = (series.code || '').toUpperCase();

    if (code === 'TEST' || code === 'TEST/5DAY') {
      d.setDate(d.getDate() + 4);
    } else if (code === '4DAY') {
      d.setDate(d.getDate() + 3);
    }
    return d;
  };

  // ---- De-dup logic you already had ----
  const uniqueSeriesMap = new Map();

  allSeries.forEach((s) => {
    const baseKey = `${s.league_id}_${s.season_id}_${s.code}_${s.league}_${s.season}`;
    const key = `${baseKey}_${s.start_date}_${s.end_date}`;

    // Check if another entry with same baseKey exists whose stage_id is +1 or -1 of current
    const existingEntry = Array.from(uniqueSeriesMap.entries()).find(([k, val]) => {
      const [ek] = k.split(`_${val.start_date}_${val.end_date}`);
      const isSameBase = ek === baseKey;
      const stageMatch = Array.isArray(val.id) && Array.isArray(s.id)
        ? val.id.some((id1) => s.id.some((id2) => Math.abs(id1 - id2) === 1))
        : Math.abs((Array.isArray(val.id) ? val.id[0] : val.id) - (Array.isArray(s.id) ? s.id[0] : s.id)) === 1;

      return isSameBase && stageMatch;
    });

    if (!existingEntry) {
      uniqueSeriesMap.set(key, s);
    }
  });

  const uniqueSeries = Array.from(uniqueSeriesMap.values());

  const filtered = uniqueSeries.filter((s) =>
    s.name?.toLowerCase().includes(search.toLowerCase()) ||
    s.league?.toLowerCase().includes(search.toLowerCase())
  );

  function seriesName(series) {
    const overrideLeagues = ['ICC World Test Championship', 'One Day International', 'Twenty20 International'];
    const validTypes = ['T20', 'T10', 'ODI', 'T20I', '4day', 'Test', 'Test/5day', 'List A'];
    const showType = validTypes.includes(series.series_type) ? ` (${series.series_type})` : '';

    const nameOnlyKeywords = ['Regular', 'Play Offs', 'County', 'Group'];
    const name = nameOnlyKeywords.includes(series.name)
      ? `${series.league}${showType}`
      : `${series.league}`;

    const baseName = overrideLeagues.includes(series.league)
      ? `${series.name}${showType}`
      : name;

    return `${baseName} ${series.season}`;
  }

  if (loading) return <div>Loading...</div>;

  return (
    <div className="series-container">
      <h1>All Series</h1>
      <input
        type="text"
        placeholder="Search series..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="series-search"
      />

      <div className="series-grid">
        {filtered.map(series => {
          const end = adjustedEndDate(series);
          const startLabel = formatDate(series.start_date);
          const endLabel = end ? format(end, 'dd MMM yyyy') : formatDate(series.end_date);

          return (
            <Link
              key={series.id}
              to={`/series/${series.id}/${series.season_id}`}
              state={{
                seriesLabel: seriesName(series),
                seriesCode: series.code,
                stageIds: series.stage_ids
              }}
              className="series-card"
            >
              <div className="series-info">
                {series?.series_type && (
                  <span className="series-type-tag">{series.series_type}</span>
                )}
                <h4>{seriesName(series)}</h4>

                {/* NEW: show start & end date on the card */}
                {(startLabel || endLabel) && (
                  <div className="series-dates">
                    {startLabel && <span className="series-date">{startLabel}</span>}
                    {(startLabel && endLabel) && <span className="series-date-sep"> â€” </span>}
                    {endLabel && <span className="series-date">{endLabel}</span>}
                  </div>
                )}
              </div>
            </Link>
          );
        })}
      </div>
    </div>
  );
}
