import React, { useMemo, useState } from 'react';
import { Link } from 'react-router-dom';
import { useSeries } from './../context/SeriesContext';
import './css/Series.css';

// Same extension rule you use in SeriesMatchesTab (applied to last match type)
// Here we approximate by series.code; in practice for Test/4Day series this matches.
const extendEndByCode = (dateStr, code = '') => {
  if (!dateStr) return null;
  const d = new Date(dateStr);
  const c = String(code || '').toUpperCase();
  if (c === 'TEST' || c === 'TEST/5DAY') d.setDate(d.getDate() + 4);
  else if (c === '4DAY') d.setDate(d.getDate() + 3);
  return d;
};

const fmt = (d) => {
  if (!d) return '';
  const dd = d instanceof Date ? d : new Date(d);
  if (isNaN(dd.getTime())) return '';
  // Compact, locale-friendly label (e.g., 31 Aug 2025)
  return dd.toLocaleDateString('en-GB', { day: '2-digit', month: 'short', year: 'numeric' });
};

export default function Series() {
  const { allSeries, loading } = useSeries();
  const [search, setSearch] = useState('');

  /**
   * Merge adjacent stages (+/- 1) that are the same league, season, code, league name, season label.
   * - Keep MIN start_date and MAX end_date across merged items
   * - Collect all stage IDs so the matches tab can still derive the same range
   * - Use the first (lowest) stage id as the link target; SeriesMatchesTab already adds +1 if needed
   */
  const mergedSeries = useMemo(() => {
    const byKey = new Map();

    const getStageIds = (s) => {
      // series objects sometimes have id as number or array; also may expose stage_ids
      if (Array.isArray(s.stage_ids) && s.stage_ids.length) return [...s.stage_ids];
      if (Array.isArray(s.id)) return [...s.id];
      return [s.id];
    };

    allSeries.forEach((s) => {
      const baseKey = `${s.league_id}_${s.season_id}_${s.code}_${s.league}_${s.season}`;

      // If an entry already exists for this baseKey, we may merge it
      const existing = byKey.get(baseKey);

      if (!existing) {
        byKey.set(baseKey, {
          ...s,
          // normalize to arrays for easy merging
          _stageIds: getStageIds(s),
          _start: s.start_date ? new Date(s.start_date) : null,
          _end:   s.end_date ? new Date(s.end_date) : null,
        });
      } else {
        // Merge if any stage is adjacent (+/-1) to any existing stage
        const newIds = getStageIds(s);
        const hasAdjacent =
          existing._stageIds.some(id1 =>
            newIds.some(id2 => Math.abs(Number(id1) - Number(id2)) === 1)
          );

        if (hasAdjacent) {
          // Merge ranges and ids
          existing._stageIds = Array.from(new Set([...existing._stageIds, ...newIds])).sort((a,b) => a - b);
          const sStart = s.start_date ? new Date(s.start_date) : null;
          const sEnd   = s.end_date ? new Date(s.end_date) : null;

          if (sStart && (!existing._start || sStart < existing._start)) existing._start = sStart;
          if (sEnd   && (!existing._end   || sEnd   > existing._end))   existing._end   = sEnd;

          // Nothing else to set: we keep the original entry’s other fields
          byKey.set(baseKey, existing);
        } else {
          // Not adjacent; treat as separate block (use a unique suffix to keep distinct)
          const key2 = `${baseKey}__${s.start_date || 'x'}__${s.end_date || 'y'}`;
          byKey.set(key2, {
            ...s,
            _stageIds: getStageIds(s),
            _start: s.start_date ? new Date(s.start_date) : null,
            _end:   s.end_date ? new Date(s.end_date) : null,
          });
        }
      }
    });

    // Build final array and extend end date by format code, to match tab behavior
    const out = Array.from(byKey.values()).map(item => {
      const firstStageId = Math.min(...item._stageIds.map(Number));
      const extendedEnd = extendEndByCode(item._end, item.code) || item._end;

      return {
        ...item,
        stage_ids: item._stageIds,
        firstStageId,
        startLabel: fmt(item._start),
        endLabel: fmt(extendedEnd),
      };
    });

    // Optional: stable sort by most-recent start date desc
    out.sort((a, b) => (b._start?.getTime() || 0) - (a._start?.getTime() || 0));

    return out;
  }, [allSeries]);

  const filtered = useMemo(() => {
    const q = search.trim().toLowerCase();
    if (!q) return mergedSeries;
    return mergedSeries.filter((s) =>
      s.name?.toLowerCase().includes(q) || s.league?.toLowerCase().includes(q)
    );
  }, [mergedSeries, search]);

  function seriesName(series) {
    const overrideLeagues = ['ICC World Test Championship', 'One Day International', 'Twenty20 International'];
    const validTypes = ['T20', 'T10', 'ODI', 'T20I', '4day', 'Test', 'Test/5day', 'List A'];
    const showType = validTypes.includes(series.series_type) ? ` (${series.series_type})` : '';

    const nameOnlyKeywords = ['Regular', 'Play Offs', 'County', 'Group'];
    const name = nameOnlyKeywords.includes(series.name)
      ? `${series.league}${showType}`
      : `${series.league}`;

    const baseName = overrideLeagues.includes(series.league)
      ? `${series.name}${showType}`
      : name;

    return `${baseName} ${series.season}`;
  }

  if (loading) return <div>Loading...</div>;

  return (
    <div className="series-container">
      <h1>All Series</h1>

      <input
        type="text"
        placeholder="Search series..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        className="series-search"
      />

      <div className="series-grid">
        {filtered.map(series => (
          <Link
            key={`${series.firstStageId}-${series.season_id}`}
            to={`/series/${series.firstStageId}/${series.season_id}`}
            state={{
              seriesLabel: seriesName(series),
              seriesCode: series.code,
              stageIds: series.stage_ids,    // pass all merged stage IDs (tab still supports deriving +1)
            }}
            className="series-card"
          >
            <div className="series-info">
              {series?.series_type && (
                <span className="series-type-tag">{series.series_type}</span>
              )}
              <h4>{seriesName(series)}</h4>

              {/* Start & End dates that MATCH SeriesMatchesTab */}
              {(series.startLabel || series.endLabel) && (
                <div className="series-dates">
                  {series.startLabel && <span className="series-date">{series.startLabel}</span>}
                  {(series.startLabel && series.endLabel) && <span className="series-date-sep"> — </span>}
                  {series.endLabel && <span className="series-date">{series.endLabel}</span>}
                </div>
              )}
            </div>
          </Link>
        ))}
      </div>
    </div>
  );
}
