import React, { useState } from 'react';
import ScorecardTab from './match/ScorecardTab';
import CommentaryTab from './match/CommentaryTab';
import LineupTab from './match/LineupTab';
import OversSummaryTab from './match/OversSummaryTab';
import PartnershipTab from './match/PartnershipTab';
import NewsTab from './match/NewsTab';
import HighlightsTab from './match/HighlightsTab';

export default function MatchDetailTabs({ matchId, matchInfo }) {
  const [activeTab, setActiveTab] = useState('highlights'); // Default to 'highlights'

  const tabs = {
    highlights: <HighlightsTab matchId={matchId} matchInfo={matchInfo} />
  };

  console.log(matchInfo.batting);

  // Add other tabs conditionally
  if (Array.isArray(matchInfo.batting) && matchInfo.batting.length > 0) {
    tabs.scorecard = <ScorecardTab matchId={matchId} matchInfo={matchInfo} />;
    tabs.commentary = <CommentaryTab matchId={matchId} matchInfo={matchInfo} />;
    tabs.lineup = <LineupTab matchId={matchId} matchInfo={matchInfo} />;
    tabs.overs = <OversSummaryTab matchId={matchId} matchInfo={matchInfo} />;
    tabs.partnership = <PartnershipTab matchId={matchId} matchInfo={matchInfo} />;
    tabs.news = <NewsTab matchId={matchId} matchInfo={matchInfo} />;
  }

  // Determine label for the highlights tab
  const highlightTabLabel = matchInfo.status === 'NS' ? 'Match Preview' : 'Highlights';

  return (
    <div>
      <div className="tabs">
        {Object.keys(tabs).map((tab) => {
          let label =
            tab === 'highlights'
              ? highlightTabLabel
              : tab.charAt(0).toUpperCase() + tab.slice(1);

          return (
            <button
              key={tab}
              className={activeTab === tab ? 'active' : ''}
              onClick={() => setActiveTab(tab)}
              style={{ marginRight: '10px' }}
            >
              {label}
            </button>
          );
        })}
      </div>
      <div className="tab-content" style={{ marginTop: '20px' }}>
        {tabs[activeTab]}
      </div>
    </div>
  );
}
