import React, { useEffect, useState } from 'react';
import './css/FeaturedMatches.css';
import axios from 'axios';

const FeaturedMatches = () => {
  const [allMatches, setAllMatches] = useState([]);
  const [stages, setStages] = useState([]);
  const [activeStageId, setActiveStageId] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
const fetchMatches = async () => {
  try {
    const { data } = await axios.get('/api/schedule');
    const matchesArray = Array.isArray(data) ? data : data.matches || [];
    setAllMatches(matchesArray);

    const uniqueStagesMap = new Map();
    matchesArray.forEach((match) => {
      const stageId = match.stage?.id;
      if (stageId && !uniqueStagesMap.has(stageId)) {
        uniqueStagesMap.set(stageId, {
          id: stageId,
          label: match.stage.name,
        });
      }
    });

    setStages(Array.from(uniqueStagesMap.values()));
  } catch (error) {
    console.error('Error fetching matches:', error);
  } finally {
    setLoading(false);
  }
};


    fetchMatches();
  }, []);

  const filteredMatches = allMatches.filter((match) => {
    const isLive =
      match?.status === 'LIVE' ||
      match?.status === 'Break' ||
      match?.status === 'Lunch' ||
      match?.status === 'Dinner' ||
      match?.status === 'Delayed' ||
      match?.status === 'Interrupted';
    return isLive;
  });

  const visibleMatches = filteredMatches.filter((match) => {
    if (!activeStageId) return true;
    return match.stage?.id === activeStageId;
  });

  if (loading) return <div>Loading matches...</div>;

  return (
    <div className="featured-matches">
      <div className="stage-tabs">
        <span
          className={`tab ${activeStageId === null ? 'active-tab' : ''}`}
          onClick={() => setActiveStageId(null)}
        >
          All
        </span>
        {stages.map((stage) => (
          <span
            key={stage.id}
            className={`tab ${activeStageId === stage.id ? 'active-tab' : ''}`}
            onClick={() => setActiveStageId(stage.id)}
          >
            {stage.label}
          </span>
        ))}
      </div>

      <div className="matches-container">
        {visibleMatches.length === 0 ? (
          <div className="no-matches">No matches available</div>
        ) : (
          visibleMatches.map((match) => (
            <div key={match.id} className="match-card">
              <div className="match-header">
                {match.stage?.name} - {match.venue?.name}
              </div>
              <div className="teams">
                <div className="team">{match.localteam?.name}</div>
                <div className="vs">vs</div>
                <div className="team">{match.visitorteam?.name}</div>
              </div>
              <div className="status">{match.status}</div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default FeaturedMatches;
