import React from 'react';
import { useNavigate } from 'react-router-dom';
import NotificationButton from './NotificationButton';
import CountdownTimer from './CountdownTimer';
import { getTeamRuns } from './../utils/matchUtils';
import './../css/Matches.css'; // reuse same styles

export default function TeamsUCMatchCard({
  matches = [],
  getFlagName,
  getTeamName,
  getVenueName,
  getVenueCity,
  seriesDefaultLabel,
  playerNames,
  upcomingMatchStatuses
}) {
  const navigate = useNavigate();

  return (
    <div className="match-list">
      {matches.map((match) => (
        <div
          key={match.id}
          className="match-card"
          onClick={() => navigate(`/match/${match.id}`, { state: { from: 'Teams' } })}
        >
          <div className="match-header-top">
            {match.type && <span className="match-type-tag">{match.type}</span>}
            {upcomingMatchStatuses.includes(match.status) && <NotificationButton match={match} />}
          </div>

          <h4>{seriesDefaultLabel(match)}</h4>

          <>
            <p>
              <img src={getFlagName(match.localteam_id)} alt="" className="flag-icon" />
              {getTeamName(match.localteam_id)}{' '}
              {getTeamRuns(match, match.localteam_id) && getTeamRuns(match, match.localteam_id)}
            </p>
            <p>
              <img src={getFlagName(match.visitorteam_id)} alt="" className="flag-icon" />
              {getTeamName(match.visitorteam_id)}{' '}
              {getTeamRuns(match, match.visitorteam_id) && getTeamRuns(match, match.visitorteam_id)}
            </p>
          </>

          <p className="status">{match.note}</p>

          {match.status === 'NS' && <CountdownTimer startTime={match.starting_at} />}

          {match.status === 'Finished' && (
            <p className="status">
              Man of the match:{' '}
              {match.manofmatch?.fullname || playerNames[match.man_of_match_id] || 'N/A'}
            </p>
          )}

          <div className="match-meta">
            {getVenueName(match.venue_id) && getVenueCity(match.venue_id) ? (
              <span className="venue">
                Venue: {getVenueName(match.venue_id)}, {getVenueCity(match.venue_id)}
              </span>
            ) : (
              <span>Venue: TBD</span>
            )}
          </div>

          <div className="notify-btn">
            {match.id && !upcomingMatchStatuses.includes(match.status) && (
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  navigate(`/match/${match.id}`);
                }}
              >
                Scorecard
              </button>
            )}

            {match?.id && match?.stage_id && match?.season_id && (
              <button
                onClick={(e) => {
                  e.stopPropagation();
                  navigate(`/series/${match.stage_id}/${match.season_id}`, {
                    state: {
                      seriesLabel: seriesDefaultLabel(match),
                      seriesCode: match?.type,
                    },
                  });
                }}
              >
                Series
              </button>
            )}
          </div>
        </div>
      ))}
    </div>
  );
}
