import React, { useEffect, useRef, useState, useMemo } from 'react';
import { useSchedule } from './../../context/ScheduleContext';
import {
  liveStatuses,
  upcomingMatchStatuses,
  completedMatchStatuses,
} from './../../constants/matchStatusConstants';
import MatchCard from './../MatchCard';
import './css/FeaturedMatches.css';

const FeaturedMatches = () => {
  const { matches = [], loading } = useSchedule();
  const [allMatches, setAllMatches] = useState([]);
  const [stages, setStages] = useState([]);
  const [activeStage, setActiveStage] = useState(null);
  const [pageCount, setPageCount] = useState(0);
  const [currentPage, setCurrentPage] = useState(0);
  const scrollRef = useRef();

  const getStageKey = (match) => `${match.stage?.id}-${match.league?.id}`;
  const getStageLabel = (match) => {
    const stageName = match.stage?.name || '';
    const leagueName = match.league?.name || '';
    if (/Regular|Play Offs|County|Group/i.test(stageName) && leagueName) {
      return `${leagueName} (${stageName})`;
    }
    return stageName || leagueName || 'Unknown';
  };

  useEffect(() => {
    if (loading || !Array.isArray(matches)) return;

    const live = [];
    const upcoming = [];
    const completed = [];

    const now = Date.now();
    const next48h = now + 48 * 60 * 60 * 1000;
    const past48h = now - 2 * 24 * 60 * 60 * 1000;

    matches.forEach((m) => {
      const matchDate = new Date(m.starting_at);
      const isLive = liveStatuses.includes(m.status);
      const isUpcoming = upcomingMatchStatuses.includes(m.status) && matchDate >= now && matchDate <= next48h;
      const isCompleted = completedMatchStatuses.includes(m.status) && matchDate >= past48h;

      if (isLive) live.push(m);
      else if (isUpcoming) upcoming.push(m);
      else if (isCompleted) completed.push(m);
    });

    const sortByDate = (a, b) => new Date(a.starting_at) - new Date(b.starting_at);
    live.sort(sortByDate);
    upcoming.sort(sortByDate);
    completed.sort(sortByDate);

    const result = [];
    const stageMatchMap = {};
    const stageSet = new Set();

    const processMatches = (matchArray, maxPerStage) => {
      for (const match of matchArray) {
        const key = getStageKey(match);
        const label = getStageLabel(match);

        if (!stageMatchMap[key]) {
          stageMatchMap[key] = [];
          stageSet.add({ key, label });
        }

        if (maxPerStage === Infinity || stageMatchMap[key].length < maxPerStage) {
          const annotatedMatch = { ...match, __stageKey: key };
          stageMatchMap[key].push(annotatedMatch);
          result.push(annotatedMatch);
        }
      }
    };

    processMatches(live, Infinity);
    processMatches(upcoming, 2);
    processMatches(completed, 2);

    setAllMatches(result);
    setStages(Array.from(stageSet));
  }, [matches, loading]);

  const visibleMatches = useMemo(() => {
    if (!activeStage) return allMatches;
    return allMatches.filter((match) => match.__stageKey === activeStage);
  }, [allMatches, activeStage]);

  useEffect(() => {
    const container = scrollRef.current;
    if (!container) return;

    const handleResize = () => {
      const totalScrollWidth = container.scrollWidth;
      const visibleWidth = container.clientWidth;
      const totalPages = Math.ceil(totalScrollWidth / visibleWidth);
      setPageCount(totalPages);
    };

    const handleScroll = () => {
      const scrollLeft = container.scrollLeft;
      const page = Math.round(scrollLeft / container.clientWidth);
      setCurrentPage(page);
    };

    container.addEventListener('scroll', handleScroll);
    window.addEventListener('resize', handleResize);
    handleResize();

    return () => {
      container.removeEventListener('scroll', handleScroll);
      window.removeEventListener('resize', handleResize);
    };
  }, [visibleMatches]);

  return (
    <div className="featured-container">
      <div className="featured-tabs-row">
        <div className="featured-tabs-left">
          <span
            className={`tab ${!activeStage ? 'active-tab' : ''}`}
            onClick={() => setActiveStage(null)}
          >
            All Featured Matches
          </span>

          {stages.map(({ key, label }) => (
            <span
              key={key}
              className={`tab ${activeStage === key ? 'active-tab' : ''}`}
              onClick={() => setActiveStage(key)}
            >
              {label}
            </span>
          ))}
        </div>
        <a href="/schedule" className="see-all-btn">All</a>
      </div>

      <div className="match-cards-scroll-wrapper" ref={scrollRef}>
        <div className={`match-cards-scroll ${visibleMatches.length < 3 ? 'center' : ''}`}>
          {visibleMatches.map((match) => (
            <MatchCard key={match.id} match={match} />
          ))}
        </div>
      </div>

      <div className="pagination-dots">
        {Array.from({ length: Math.max(1, pageCount - 1) }).map((_, i) => (
          <span
            key={i}
            className={`dot ${i === currentPage ? 'active' : ''}`}
          />
        ))}
      </div>
    </div>
  );
};

export default FeaturedMatches;
