import React, { useEffect, useMemo, useState } from 'react';
import { useParams } from 'react-router-dom';
import './css/PlayerDetails.css';

export default function PlayerDetails() {
  const { id } = useParams();
  const [playerInfo, setPlayerInfo] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPlayer = async () => {
      try {
        const res = await fetch(`/api/player/${id}`);
        const data = await res.json();
        setPlayerInfo(data?.data?.data || null);
      } catch (err) {
        console.error('Error fetching player:', err);
      } finally {
        setLoading(false);
      }
    };
    fetchPlayer();
  }, [id]);

  if (loading) return <div className="player-details">Loading...</div>;
  if (!playerInfo) return <div className="player-details">No player data.</div>;

  const {
    fullname,
    image_path,
    country,
    battingstyle,
    bowlingstyle,
    dateofbirth,
    position,
    career = [],
  } = playerInfo;

  // ---------- helpers ----------
  const normalizeFormat = (t = '') => {
    const tl = t.toLowerCase();
    if (tl.startsWith('test')) return 'Test';  // unify "Test", "Test/5day"
    return t; // keep T20 vs T20I as-is
  };

  const seasonKey = (name = '') => {
    // Use right-most year in a range "2019/2021" or a single year "2023"
    const m = String(name).match(/(\d{4})(?:\s*\/\s*(\d{4}))?/);
    if (!m) return -Infinity;
    return m[2] ? Number(m[2]) : Number(m[1]);
  };

  const to2 = (v) => (v == null || isNaN(v) ? '—' : Number(v).toFixed(2));
  const showIfNum = (v) => (v == null || isNaN(v) ? '—' : String(v));
  const bowlingAvg = (avg, wickets) => (wickets > 0 ? to2(avg) : '—');
  const bowlingSR  = (sr,  wickets) => (wickets > 0 ? to2(sr)  : '—');

  // ---------- prepare tables ----------
  const { battingRows, bowlingRows } = useMemo(() => {
    const bat = [];
    const bowl = [];

    (career || []).forEach((c) => {
      const type = normalizeFormat(c?.type || '');
      const seasonName = c?.season?.name || ''; // keep original label if provided
      if (c?.batting) {
        bat.push({
          type,
          seasonName,
          matches: c.batting.matches,
          innings: c.batting.innings,
          runs: c.batting.runs_scored,
          notOuts: c.batting.not_outs,
          high: c.batting.highest_inning_score,
          avg: c.batting.average,
          sr: c.batting.strike_rate,
        });
      }
      if (c?.bowling) {
        bowl.push({
          type,
          seasonName,
          matches: c.bowling.matches,
          innings: c.bowling.innings,
          wickets: c.bowling.wickets,
          avg: c.bowling.average,
          econ: c.bowling.econ_rate,
          sr: c.bowling.strike_rate,
          five: c.bowling.five_wickets,
          ten: c.bowling.ten_wickets,
        });
      }
    });

    // sort by season (desc) inside each table
    bat.sort((a, b) => seasonKey(b.seasonName) - seasonKey(a.seasonName));
    bowl.sort((a, b) => seasonKey(b.seasonName) - seasonKey(a.seasonName));

    return { battingRows: bat, bowlingRows: bowl };
  }, [career]);

  return (
    <div className="player-details">
      <div className="player-header">
        {image_path && <img src={image_path} alt={fullname} />}
        <div>
          <h2>{fullname}</h2>
          <p><strong>Country:</strong> {country?.name || '—'}</p>
          {position?.name && <p><strong>Position:</strong> {position.name}</p>}
          {battingstyle && <p><strong>Batting Style:</strong> {battingstyle}</p>}
          {bowlingstyle && <p><strong>Bowling Style:</strong> {bowlingstyle}</p>}
          {dateofbirth && <p><strong>Date of Birth:</strong> {dateofbirth}</p>}
        </div>
      </div>

      {battingRows.length > 0 && (
        <div className="career-section">
          <h3>Batting Stats</h3>
          <table className="career-table">
            <thead>
              <tr>
                <th>Format</th>
                <th>Year</th>
                <th>Matches</th>
                <th>Innings</th>
                <th>Runs</th>
                <th>Not Outs</th>
                <th>High Score</th>
                <th>Average</th>
                <th>Strike Rate</th>
              </tr>
            </thead>
            <tbody>
              {battingRows.map((r, i) => (
                <tr key={`bat-${i}`}>
                  <td>{r.type}</td>
                  <td>{r.seasonName || '—'}</td>
                  <td>{showIfNum(r.matches)}</td>
                  <td>{showIfNum(r.innings)}</td>
                  <td>{showIfNum(r.runs)}</td>
                  <td>{showIfNum(r.notOuts)}</td>
                  <td>{showIfNum(r.high)}</td>
                  <td>{to2(r.avg)}</td>
                  <td>{to2(r.sr)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {bowlingRows.length > 0 && (
        <div className="career-section">
          <h3>Bowling Stats</h3>
          <table className="career-table">
            <thead>
              <tr>
                <th>Format</th>
                <th>Year</th>
                <th>Matches</th>
                <th>Innings</th>
                <th>Wickets</th>
                <th>Avg</th>
                <th>Economy</th>
                <th>Strike Rate</th>
                <th>5W</th>
                <th>10W</th>
              </tr>
            </thead>
            <tbody>
              {bowlingRows.map((r, i) => (
                <tr key={`bowl-${i}`}>
                  <td>{r.type}</td>
                  <td>{r.seasonName || '—'}</td>
                  <td>{showIfNum(r.matches)}</td>
                  <td>{showIfNum(r.innings)}</td>
                  <td>{showIfNum(r.wickets)}</td>
                  <td>{bowlingAvg(r.avg, r.wickets)}</td>
                  <td>{to2(r.econ)}</td>
                  <td>{bowlingSR(r.sr, r.wickets)}</td>
                  <td>{showIfNum(r.five)}</td>
                  <td>{showIfNum(r.ten)}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}
